import React from "react";
import { render } from 'react-dom';
import Gallery from 'react-grid-gallery';
import ImgsViewer from "react-images-viewer";

export default class PhotosPost extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            images: [
                { id: 1, src: "/images/ahmed1.jpg", name: "Macline" },
                { id: 2, src: "/images/ahsan1.jpg", name: "ahsan32" },
                { id: 3, src: "/images/babar1.jpg", name: "babar42" },
                { id: 4, src: "/images/fawad1.jpg", name: "fawad2" },
                { id: 5, src: "/images/feroz1.jpg", name: "feroz432" },
                { id: 6, src: "/images/0.jfif", name: "adil5" },
                { id: 7, src: "/images/imrankhan1.jpg", name: "imran4" },
                { id: 8, src: "/images/imrankhan3.jpg", name: "khan4" },
                { id: 9, src: "/images/imrankhanpost.jpg", name: "alikhan4" },
                { id: 10, src: "/images/feroz1.jpg", name: "feroz432" },
                { id: 11, src: "/images/0.jfif", name: "adil5" },
                { id: 12, src: "/images/imrankhan1.jpg", name: "imran4" },
                { id: 13, src: "/images/imrankhan3.jpg", name: "khan4" },
                { id: 14, src: "/images/imrankhanpost.jpg", name: "alikhan4" },
            ],
            currImg: false,
            viewerIsOpen: false,

        }
    }

    gotoPrevious() { }
    gotoNext() { }
    closeViewer() { }

    render() {
        return (
            <ImgsViewer
                imgs={[
                    { src: "http://example.com/img1.jpg" },
                    { src: "http://example.com/img2.png" },
                ]}
                currImg={this.state.currImg}
                isOpen={this.state.viewerIsOpen}
                onClickPrev={this.gotoPrevious}
                onClickNext={this.gotoNext}
                onClose={this.closeViewer}
            />
        );
    }
}



