import React, { useState, useEffect } from "react";
import { ReactSession } from 'react-client-session';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Sparkles from 'react-sparkle';
import Paper from "@material-ui/core/Paper";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
// custom feeds
import PostFeeds from '../src/post/PostFeeds';
import ConfirmModal from "./AddPostModal/ConfirmModal";
import Modal from "react-modal";
import "./App.css";
// get data
import axios from 'axios';


const HomeScreen = () => {

    const type_user = localStorage.getItem('user_type');
    const [isLoading, setIsLoading] = React.useState(true);
    const [value, setValue] = React.useState(0);
    const [confirmOpen, setConfirmOpen] = React.useState(false);
    const [isOpen, setIsOpen] = useState(false);

    function toggleModal() {
        setIsOpen(!isOpen);
    }

    //creating IP state
    const [ip, setIP] = useState('');

    //creating function to load ip address from the API
    const getData = async () => {
        const res = await axios.get('https://geolocation-db.com/json/')
        console.log(res.data);
        setIP(res.data.IPv4)
        setUserData(res.data.IPv4);
    }

    const setUserData = async (ip_me) => {
        const data = {
            ip_address: ip_me,
        }
        axios.post('https://amazingtech.space/PTI/api/login', data)
            .then((res) => {
                console.log('resp', JSON.stringify(res.data));
                if (res.data.status === true) {
                    localStorage.setItem("user_type", 'local_user');
                    localStorage.setItem("user_ip", res.data.id);
                    localStorage.setItem("ip_address", res.data.ip_address);
                    toast.success("Logged in successfully.", {
                        position: "top-center",
                        autoClose: 1500,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });
                } else {
                    toast.error(res.data.message, {
                        position: "No valid user!",
                        autoClose: 3000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });
                }
            })
            .catch((err) => console.log('err', JSON.stringify(err)));

    }

    useEffect(() => {
        //passing getData method to the lifecycle method
        if (!localStorage.getItem("user_ip")) {
            getData();
        }
    }, [])

    const handleLogout = () => {
        console.log("this hould logout the user");
    };


    return (
        <div>
            <div className="main-bg">
                <div className="frame-holder">
                    <div className="content-wrapper">
                        <div className="card-block">
                            {type_user !== 'local_user' ? <div className="clickForPopUp" onClick={() => setConfirmOpen(true)}>
                                <img style={{ width: 22, }} src="./assets/images/add_post.png" className="img-responsive navbar__icons" />
                            </div> : null}
                            <div className="card-header">
                                <div className="col-sm-2">
                                    <div className="main-logo">
                                        <Sparkles
                                            color={'#00867b'}
                                            count={20}
                                            minSize={7}
                                            maxSize={9}
                                            overflowPx={10}
                                            fadeOutSpeed={10}
                                            flicker={false}
                                        />
                                        <img src="./assets/images/logo.svg" className="img-responsive" alt="logo" />
                                    </div>
                                </div>
                                <Paper className="col-sm-7" style={{
                                    background: '#f7f7f7',
                                    boxShadow: 'none',
                                    height: 50,
                                    flexDirection: 'row',
                                    flexWrap: 'nowrap',
                                    justifyContent: 'space-between',
                                    alignItems: 'center',
                                    width: '70',
                                }}
                                    square
                                >
                                    <Tabs
                                        value={value}
                                        textColor="primary"
                                        indicatorColor="primary"
                                        onChange={(event, newValue) => {
                                            setValue(newValue);
                                        }} >
                                        <Tab label="All" />
                                        <Tab label="Photos" />
                                        <Tab label="Videos" />
                                        <Tab label="Tranding Photos" />
                                    </Tabs>
                                    {PostFeeds(value)}
                                </Paper>
                                <div className="col-sm-3">
                                    <div className="extraBox" style={{ display: 'flex', justifyContent: 'flex-end' }}>
                                        <li>
                                            <img style={{ width: 22, }} src="./assets/images/bell.png" className="img-responsive navbar__icons" />
                                        </li>
                                        <li>
                                            <div className="dropdown">
                                                <div className="btn-dropdown-ellipse dropdown-toggle" type="link" data-toggle="dropdown">
                                                    <img style={{ width: 22, }} src="./assets/images/menu.png" className="img-responsive navbar__icons" />
                                                </div>
                                                <ul className="dropdown-menu left arrow-top-right">
                                                    <li><a to="/Login"><i className="fa fa-bell" aria-hidden="true"></i>Update Notification</a></li>
                                                    <li><a href="/Login"><i className="fa fa-bullseye" aria-hidden="true"></i>Action Notification</a></li>
                                                    <li><a href="/Login"><i className="fa fa-sign-in" aria-hidden="true"></i>Login</a></li>
                                                    <li><a href="/Login"><i className="fa fa-facebook-square" aria-hidden="true"></i>Our Facebook</a></li>
                                                    <li><a href="/Login"><i className="fa fa-twitter-square" aria-hidden="true"></i>Our Twitter</a></li>
                                                    <li><a href="/AboutUs"><i className="fa fa-info-circle" aria-hidden="true"></i>About</a></li>
                                                </ul>
                                            </div>
                                        </li>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div>
                        <ConfirmModal
                            content="Are you sure you want to leeeave us ?"
                            open={confirmOpen}
                            setOpen={setConfirmOpen}
                            onConfirm={handleLogout}
                        />
                    </div>
                    <Modal
                        isOpen={isOpen}
                        onRequestClose={toggleModal}
                        contentLabel="My dialog" >
                        <div>My modal dialog.</div>
                        <button onClick={toggleModal}>Close modal</button>
                    </Modal>
                </div>
            </div>
            <ToastContainer />
        </div>
    );

}

export default HomeScreen;

