  backgroundColor: "white",
  borderRadius: "0.5rem",
  borderBottomLeftRadius: 0,
  borderBottomRightRadius: 0,
};

const IconsLisStyle: CSSProperties = {
  padding: "1.25rem",
  display: "grid",
  gridTemplateColumns: "repeat(4, 1fr)",
  gridGap: "1.25rem",
};

export default function SocialIcons({
  onClose,
  sites,
  data,
  closeText,
  onClick,
}: SocialIconsProps) {
  return (
    <section
      role="dialog"
      aria-modal="true"
      className="web-share-fade-in-up"
      style={IconsContainer}
    >
      <Header title={data.title} />
      <div style={IconsLisStyle}>
        {sites.map((name) => (
          <Icon name={name} key={name} data={data} onClose={onClose} onClick={onClick} />
        ))}
      </div>
      <CloseButton onClose={onClose} closeText={closeText} />
    </section>
  );
}
import { useCallback, useState } from "react";

export default function useDisclosure(isOpenDefault = false) {
  const [isOpen, setIsOpen] = useState(isOpenDefault);

  const onOpen = useCallback(() => setIsOpen(true), []);
  const onClose = useCallback(() => setIsOpen(false), []);
  const onToggle = useCallback(() => setIsOpen((state) => !state), []);

  return { isOpen, onOpen, onClose, onToggle };
}
import { RWebShareProps } from "./interfaces";
import React, { cloneElement, useMemo } from "react";

import Backdrop from "./components/backdrop";
import iconList from "./components/icon/list";
import Portal from "./components/portal";
import SocialIcons from "./components/social-icons";
import useDisclosure from "./hooks/use-disclosure";

const defaultSites = Object.keys(iconList).slice(0, 8);

export const RWebShare = ({
  children,
  closeText,
  data,
  onClick,
  sites = defaultSites,
}: RWebShareProps) => {
  const { onOpen, onClose, isOpen } = useDisclosure();

  const shareData = useMemo(
    () => ({
      ...data,
      title: data.title || "share",
      text: data.text || "",
      url: data.url || (typeof window !== "undefined" && window.location.href) || "",
    }),
    [data]
  );

  const handleOnClick = async () => {
    if (window.navigator.share) {
      try {
        await window.navigator.share(shareData);
        onClick();
      } catch (e) {
        console.warn(e);
      }
    } else {
      onOpen();
    }
  };

  return (
    <>
      {cloneElement(children, {
        ...children?.props,
        onClick: handleOnClick,
      })}
      {isOpen && (
        <Portal>
          <Backdrop onClose={onClose}>
            <SocialIcons
              onClose={onClose}
              sites={sites}
              data={shareData}
              closeText={closeText}
              onClick={onClick}
            />
          </Backdrop>
        </Portal>
      )}
    </>
  );
};
