import * as React from 'react';
// get data
import $ from "jquery";
import axios from 'axios';
import SimpleImageSlider from "react-simple-image-slider";
import "react-responsive-modal/styles.css";
// responsive slider
import { BottomSheet } from 'react-spring-bottom-sheet';
import 'react-spring-bottom-sheet/dist/style.css';
// webshare
import { RWebShare } from "react-web-share";
import windowDimensions from '../Global/useWindowDimensions';
import 'react-spring-bottom-sheet/dist/style.css';
import ReadMoreReact from 'read-more-react';

import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Typography from '@mui/material/Typography';

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialogContent-root': {
        padding: theme.spacing(2),
    },
    '& .MuiDialogActions-root': {
        padding: theme.spacing(1),
    },
}));

const BootstrapDialogTitle = (props) => {
    const { children, onClose, ...other } = props;

    return (
        <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
            {children}
            {onClose ? (
                <IconButton
                    aria-label="close"
                    onClick={onClose}
                    sx={{
                        position: 'absolute',
                        right: 8,
                        top: 8,
                        color: (theme) => theme.palette.grey[500],
                    }}
                >
                    <CloseIcon />
                </IconButton>
            ) : null}
        </DialogTitle>
    );
};

BootstrapDialogTitle.propTypes = {
    children: PropTypes.node,
    onClose: PropTypes.func.isRequired,
};

function CustomizedDialogs() {
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <Button variant="outlined" onClick={handleClickOpen}>
                Open dialog
            </Button>
            <BootstrapDialog
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
            >
                <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
                    Modal title
                </BootstrapDialogTitle>
                <DialogContent dividers>
                    <Typography gutterBottom>
                        Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                        dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                        consectetur ac, vestibulum at eros.
                    </Typography>
                    <Typography gutterBottom>
                        Praesent commodo cursus magna, vel scelerisque nisl consectetur et.
                        Vivamus sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.
                    </Typography>
                    <Typography gutterBottom>
                        Aenean lacinia bibendum nulla sed consectetur. Praesent commodo cursus
                        magna, vel scelerisque nisl consectetur et. Donec sed odio dui. Donec
                        ullamcorper nulla non metus auctor fringilla.
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button autoFocus onClick={handleClose}>
                        Save changes
                    </Button>
                </DialogActions>
            </BootstrapDialog>
        </div>
    );
}



const AllPostScreen = () => {

    const sheetRef = React.useRef();
    const [open, setOpen] = React.useState(false);
    const [PostData, setPostData] = React.useState([]);
    const { height, width } = windowDimensions();

    let [over, setOver] = React.useState(false);

    let buttonstyle = {
        backgroundColor: ''
    }

    if (over) {
        buttonstyle.backgroundColor = "green";
    }
    else {
        buttonstyle.backgroundColor = '';
    }

    const [images] = React.useState([
        { id: 1, url: "./assets/images/ahmed1.jpg", name: "Macline" },
        { id: 2, url: "./assets/images/ahsan1.jpg", name: "ahsan32" },
        { id: 3, url: "./assets/images/babar1.jpg", name: "babar42" },
        { id: 4, url: "./assets/images/fawad1.jpg", name: "fawad2" },
        { id: 5, url: "./assets/images/feroz1.jpg", name: "feroz432" },
        { id: 6, url: "./assets/images/0.jfif", name: "adil5" },
        { id: 7, url: "./assets/images/imrankhan1.jpg", name: "imran4" },
        { id: 8, url: "./assets/images/imrankhan3.jpg", name: "khan4" },
        { id: 9, url: "./assets/images/imrankhanpost.jpg", name: "alikhan4" },
    ]);

    //creating function to load ip address from the API
    const getData = async () => {
        await axios.get('https://amazingtech.space/PTI/api/posts-list')
            .then((res) => {
                if (res.data.status === true) {
                    setPostData(res.data.post);
                    console.log('getPostData', JSON.stringify(res.data))
                }
            }
            ).catch((err) => console.log('', JSON.stringify(err)));

    }

    const onOpenModal = () => {
        setOpen(true)
    };

    const onCloseModal = () => {
        setOpen(false)
    };

    React.useEffect(() => {
        //passing getData method to the lifecycle method
        if (localStorage.getItem("user_ip")) {
            getData();
        }
    }, []);

    return (
        <>
            <div className="chat-block user-content-area">
                <div className="chat-card chat-date">
                    <p>24, June, 2022 </p>
                </div>
                {PostData.map((items, index) => {
                    return (
                        <div>
                            <div className="chat-types update-type">
                                <div className="chat-card">
                                    <div className="chat-content">
                                        <div className="chat-options">
                                            <div className="dropdown">
                                                <div className="btn-dropdown-ellipse dropdown-toggle" type="link" data-toggle="dropdown">
                                                    <img src='./assets/images/ellipses-icon.png' />
                                                </div>
                                                <ul className="dropdown-menu left arrow-top-right">
                                                    <li><a href="#"><i className="fa fa-clipboard" aria-hidden="true"></i><span className='linkText'>Copy to Clipboard</span></a></li>
                                                    <li><a href="#"><i className="fa fa-trash" aria-hidden="true"></i><span className='linkText'>Delete</span></a></li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="msg-type">
                                            <img src={'https://amazingtech.space/PTI/storage/' + items.user_image} style={{ borderRadius: 100 }} class="img-responsive" alt="" />
                                        </div>
                                        <div className="chat-text-content">
                                            <span className="message-content-style">
                                                <h6>{items.name}:</h6>
                                                <h5>{items.post_title}</h5>
                                                <ReadMoreReact
                                                    text={$(items.description).text()}
                                                    min={120}
                                                    ideal={140}
                                                    max={180}
                                                    readMoreText={'read more'} />
                                            </span>
                                            <div className="time-seen">
                                                <div className="admin-seen">
                                                    <p>Seen 200K</p>
                                                </div>
                                                <div className="message-time">
                                                    <p>{items.created_time}</p>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="sliderBox"
                                        style={{ marginTop: 0, marginBottom: 0 }}>
                                        {/* <SimpleImageSlider
                                            width={466}
                                            height={350}
                                            images={images}
                                            showBullets={true}
                                            showNavs={true}
                                        /> slider yha p es div me */}

                                    </div>
                                    <div className="chat-footer">
                                        <ul>
                                            <li>
                                                <a href="#" className="chat-like">
                                                    <i onClick={() => onOpenModal()} className="dislike-icon">
                                                        <img style={{ width: 22, height: 22 }} src='./assets/images/heart.png' />
                                                    </i>
                                                    <span style={{}} className="numberCount">8</span>
                                                </a>
                                            </li>
                                            <li>
                                                <a href="#" className="chat-dislike">
                                                    <i onClick={() => onOpenModal()} className="like-icon">
                                                        <img style={{ width: 22, height: 22 }} src='./assets/images/broken-heart.png' />
                                                    </i>
                                                    <span style={{}} className="numberCount">8</span>
                                                </a>
                                            </li>
                                            <li>
                                                <a href="#" className="chat-like">
                                                    <i onClick={() => onOpenModal()} className="fi fi-rs-heart">
                                                        <img style={{ width: 22, height: 22 }} src='./assets/images/comment-alt.png' />
                                                    </i>
                                                    <span style={{}} className="numberCount">8</span>
                                                </a>
                                            </li>
                                            <li>
                                                <div className="dropdown">
                                                    <i className="btn-dropdown-ellipse dropdown-toggle" type="link" data-toggle="dropdown">
                                                        <img style={{ width: 22, height: 22, marginTop: 5 }} src='./assets/images/share.png' />
                                                    </i>
                                                    <ul className="dropdown-menu left arrow-top-right">
                                                        <li><a href="#"><i className="fa fa-clipboard" aria-hidden="true"></i><span className='linkText'>Copy Link</span></a></li>
                                                        <li><a href="#"><i className="fa fa-facebook-square" aria-hidden="true"></i><span className='linkText'>To Facebook</span></a></li>
                                                        <li><a href="#"><i className="fa fa-twitter-square" aria-hidden="true"></i><span className='linkText'>To Twitter</span></a></li>
                                                        <li><a href="#"><i className="fa fa-whatsapp" aria-hidden="true"></i><span className='linkText'>To Whatsapp</span></a></li>
                                                    </ul>
                                                </div>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                            <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
                                {children}
                                {onClose ? (
                                    <IconButton
                                        aria-label="close"
                                        onClick={onClose}
                                        sx={{
                                            position: 'absolute',
                                            right: 8,
                                            top: 8,
                                            color: (theme) => theme.palette.grey[500],
                                        }}
                                    >
                                        <CloseIcon />
                                    </IconButton>
                                ) : null}
                            </DialogTitle>
                        </div>
                    )
                })}

                <div class="chat-types update-type video-type">
                    <div class="chat-card">
                        <div class="chat-content">
                            <div class="chat-options">
                                <div class="dropdown">
                                    <div class="btn-dropdown-ellipse dropdown-toggle" type="link" data-toggle="dropdown">
                                        <img src='./assets/images/ellipses-icon.png' />
                                    </div>
                                    <ul class="dropdown-menu left arrow-top-right">
                                        <li><a href="#"><i class="fa fa-clipboard" aria-hidden="true"></i>Copy to Clipboard</a></li>
                                        <li><a href="#"><i class="fa fa-trash" aria-hidden="true"></i>Delete</a></li>
                                    </ul>
                                </div>
                            </div>
                            <div class="msg-type">
                                <img src="./assets/images/ahmed1.jpg" style={{ borderRadius: 100 }} class="img-responsive" alt="" />
                            </div>
                            <div class="chat-text-content">
                                <span class="message-content-style">
                                    <h6>Macline Carlieo:</h6>Majority have suffered alteration randomised words which<a href="#" class="readmore">Read More</a>
                                </span>
                                <div class="time-seen">
                                    <div class="admin-seen">
                                        <p>Seen 200K</p>
                                    </div>
                                    <div class="message-time">
                                        <p>12:15 pm</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class="chat-video">
                            <div class="video-frame">
                                <video class="frame-vedio-control" controls src="./assets/images/post-video.mp4" type="video/mp4"></video>
                            </div>
                            <div class="video-play-btn"></div>
                        </div>
                        <div class="chat-footer">
                            <ul>
                                <li>
                                    <a href="#" class="chat-dislike">
                                        {true ? <i class="dislike-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/heart.gif' />
                                        </i> : <i class="dislike-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/heart.gif' />
                                        </i>}
                                        <span style={{}} className="numberCount">5</span>
                                    </a>
                                </li>
                                <li>
                                    <a href="#" class="chat-like">
                                        <i class="like-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/comments.gif' />
                                        </i>
                                        <span style={{}} className="numberCount">8</span>
                                    </a>
                                </li>
                                <li>
                                    <RWebShare
                                        title={'Share'}
                                        data={{
                                            text: "Like humans, flamingos make friends for life",
                                            url: "https://on.natgeo.com/2zHaNup",
                                            title: "Flamingos",
                                        }}
                                        onClick={() => console.log("shared successfully!")}
                                    >
                                        <i class="share-icon">
                                            <img style={{ width: 22, height: 22 }} class="img-responsive" src='./assets/images/share.png' />
                                        </i>
                                    </RWebShare>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
                <div class="chat-types action-type">
                    <div class="chat-card">
                        <div class="chat-content">
                            <div class="chat-options">
                                <div class="dropdown">
                                    <div class="btn-dropdown-ellipse dropdown-toggle" type="link" onclick="myCopy()">
                                        <img src='./assets/images/ellipses-icon.png' />
                                    </div>
                                </div>
                            </div>
                            <div class="msg-type">
                                <img src="./assets/images/ahmed1.jpg" style={{ borderRadius: 100 }} class="img-responsive" alt="" />
                            </div>
                            <div class="chat-text-content">
                                <span class="message-content-style">
                                    <h6>John Carter:</h6><span id="copyClip">Majority have suffered alteration in some form, by injected humour, or randomised words which</span><a href="#" class="readmore">Read More</a>
                                </span>
                                <div className="sliderBox" style={{ marginTop: 10, marginBottom: 10 }}>
                                    <SimpleImageSlider
                                        width={450}
                                        height={350}
                                        images={images}
                                        showBullets={true}
                                        showNavs={true}
                                    />
                                </div>
                                <div class="time-seen">
                                    <div class="admin-seen">
                                        <p>Seen 200K</p>
                                    </div>
                                    <div class="message-time">
                                        <p>12:15 pm</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class="chat-footer">
                            <ul>
                                <li>
                                    <a href="#" class="chat-dislike">
                                        {true ? <i class="dislike-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/heart.gif' />
                                        </i> : <i class="dislike-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/heart.gif' />
                                        </i>}
                                        <span style={{}} className="numberCount">5</span>
                                    </a>
                                </li>
                                <li>
                                    <a href="#" class="chat-like">
                                        <i class="like-icon">
                                            <img style={{ width: 22, height: 22 }} src='./assets/images/comments.gif' />
                                        </i>
                                        <span style={{}} className="numberCount">8</span>
                                    </a>
                                </li>
                                <li>
                                    <RWebShare
                                        title={'Share'}
                                        data={{
